document.addEventListener("DOMContentLoaded",function(){const c=document.querySelector("#burgerMenu"),o=document.querySelector("#mobileMenu"),i=o.querySelectorAll("nav > a"),r=document.querySelector("#closeMobileMenu");function t(){o.classList.toggle("hidden")}c.addEventListener("click",function(e){t()}),r.addEventListener("click",function(e){t()}),i.forEach(function(e){e.addEventListener("click",function(n){t()})}),document.querySelectorAll(".accordion-header").forEach(e=>{e.addEventListener("click",function(){const n=e.parentElement.querySelector(".accordion-content");n.classList.contains("hidden")?(n.classList.remove("hidden"),e.classList.add("_is-toggled")):(n.classList.add("hidden"),e.classList.remove("_is-toggled"))})}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(n){n.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})})});
